mapper.select:
SELECT 

FROM ws_link_category a

WHERE a.id = #{id}

SELECT 

FROM ws_link_category a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.state = #{state}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM ws_link_category a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ws_link_category SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
